var debug = require('debug')('game.js');

function Game(){
    this.countpieces = 30;
}

Game.prototype.countpieces  = 0;
Game.prototype.pieces       = [];
Game.prototype.players      = [];
// lot --->
Game.prototype.winner       = 0;
Game.prototype.lots         = [];

/*
    # Функция определяет победителя
    #
    #
*/
Game.prototype.winnerSpot = function(){
    this.lots[0] = this.randomNumber(1 , 6);
    this.lots[1] = this.randomNumber(1 , 6);
    
    var win = (this.lots[0] > this.lots[1]) ? 0 : 1;
    
    this.winner = this.players[win];
}

/*
    #
    # Генерируем фишки для игры
    #
*/
Game.prototype.generatePieces = function(){
    for(var i = 0; i < this.countpieces; i++){
        this.pieces.push(this.randomNumber(1 , 100));
    }
};

/*
    #
    # Генерация случайного числа
    #
*/
Game.prototype.randomNumber = function (min, max){
    	var rand = min - 0.5 + Math.random()*(max-min+1);
    	rand = Math.round(rand);
    	return rand;
}



module.exports = Game;