var debug = require('debug')('user');

function User(){}

User.prototype.socket   = {};
User.prototype.id       = 0;

User.prototype.setSocket = function(socketobj){
    this.socket = socketobj;
};

/*
    # Регистрация пользователя на сервере
    # 
    #
*/

User.prototype.registr = function(data){
    if(data !== undefined){
        if('id' in data){
            this.id = data.id;
            return this.id;
        }else{debug("ERROR id in data undefined");}
    }else{debug("ERROR data is undefined");}
}

User.prototype.getid = function(data){
    if(this.id !== 0){
        return this.id;
    }
}

module.exports = User;